import './App.css';
import {useState} from 'react';
import {AddInventoryPage, TopMenu} from './Components';
import {BrowserRouter, Switch, Route} from 'react-router-dom';


function App() {

  const [itemList, setItemList] = useState([]);
  const [item, setItem] = useState('');
  const [box, setBox] = useState('');


  const handleAddItem = (e) => {
    e.preventDefault();

    const itemInfo = {
      name: item,
      boxNumber: box
    }

    setItemList((prevState) => 
      [...prevState, itemInfo]
    )
  }

  return (
    <div className="App">
      <Router>
        <TopMenu />
        <Switch>
          <Route exact path="/">
            <p>Home page</p>
          </Route>
          <Route exact path="/additem">
            <AddInventoryPage 
              setItem={setItem}
              setBox={setBox}
              items={itemList}
              handleAddItem={handleAddItem}
             />
          </Route>
        </Switch>
      </Router>   
    </div>
  );
}

export default App;
